    val listOlahraga : ArrayList<OlahRagaClass>
        get() {
            val list = arrayListOf<OlahRagaClass>()
            for (position in olahragaTitle.indices){
                val olahraga = OlahRagaClass()
                olahraga.titleClass = olahragaTitle[position]
                olahraga.lokasiClass = olahragaLokasi[position]
                olahraga.synopsisClass = olahragaSynopsis[position]
                olahraga.posterClass = olahragaPoster[position]

                list.add(olahraga)
            }

            return list
        }
