    val listWisata : ArrayList<WisataClass>
        get() {
            val list = arrayListOf<WisataClass>()
            for (position in wisataTitle.indices){
                val wisata = WisataClass()
                wisata.titleClass = wisataTitle[position]
                wisata.posterClass = wisataPoster[position]
                wisata.lokasiClass = wisataLokasi[position]
